from .consts import NPM_PROD_ENDPOINT_TEMPLATE
from .consts import NPM_STAGE_ENDPOINT_TEMPLATE

def get_npm_endpoint(domain, repository, stage='prod'):
    if stage != 'prod':
        return NPM_STAGE_ENDPOINT_TEMPLATE.format(
            domain=domain, repository=repository, package_manager='npm',
            stage=stage)

    return NPM_PROD_ENDPOINT_TEMPLATE.format(
        domain=domain, repository=repository, package_manager='npm')
